// @ts-nocheck
import { render } from "solid-js/web";
import { createStore } from "solid-js/store";
import { useForm } from "./validation";
import "./styles.css";

const EMAILS = ["johnsmith@outlook.com", "mary@gmail.com", "djacobs@move.org"];

function fetchUserName(name) {
  return new Promise((resolve) => {
    setTimeout(() => resolve(EMAILS.indexOf(name) > -1), 200);
  });
}

const ErrorMessage = (props) => <span class="error-message">{props.error}</span>;

const App = () => {
  const { validate, formSubmit, errors } = useForm({
    errorClass: "error-input"
  });
  const [fields, setFields] = createStore();
  const fn = (form) => {
    // form.submit()
    console.log("Done");
  };
  const userNameExists = async ({ value }) => {
    const exists = await fetchUserName(value);
    return exists && `${value} is already being used`;
  };
  const matchesPassword = ({ value }) =>
    value === fields.password ? false : "Passwords must Match";

  return (
    <form use:formSubmit={fn}>
      <h1>Sign Up</h1>
      <div class="field-block">
        <input
          name="email"
          type="email"
          placeholder="Email"
          required
          use:validate={[userNameExists]}
        />
        {errors.email && <ErrorMessage error={errors.email} />}
      </div>
      <div class="field-block">
        <input
          type="password"
          name="password"
          placeholder="Password"
          required=""
          minlength="8"
          onInput={(e) => setFields("password", e.target.value)}
          use:validate
        />
        {errors.password && <ErrorMessage error={errors.password} />}
      </div>
      <div class="field-block">
        <input
          type="password"
          name="confirmpassword"
          placeholder="Confirm Password"
          required=""
          use:validate={[matchesPassword]}
        />
        {errors.confirmpassword && (
          <ErrorMessage error={errors.confirmpassword} />
        )}
      </div>

      <button type="submit">Submit</button>
    </form>
  );
};

render(App, document.getElementById("app"));

import { createStore } from "solid-js/store";

function checkValid({ element, validators = [] }, setErrors, errorClass) {
  return async () => {
    element.setCustomValidity("");
    element.checkValidity();
    let message = element.validationMessage;
    if (!message) {
      for (const validator of validators) {
        const text = await validator(element);
        if (text) {
          element.setCustomValidity(text);
          break;
        }
      }
      message = element.validationMessage;
    }
    if (message) {
      errorClass && element.classList.toggle(errorClass, true);
      setErrors({ [element.name]: message });
    }
  };
}

export function useForm({ errorClass }) {
  const [errors, setErrors] = createStore({}),
    fields = {};

  const validate = (ref, accessor) => {
    const validators = accessor() || [];
    let config;
    fields[ref.name] = config = { element: ref, validators };
    ref.onblur = checkValid(config, setErrors, errorClass);
    ref.oninput = () => {
      if (!errors[ref.name]) return;
      setErrors({ [ref.name]: undefined });
      errorClass && ref.classList.toggle(errorClass, false);
    };
  };

  const formSubmit = (ref, accessor) => {
    const callback = accessor() || (() => {});
    ref.setAttribute("novalidate", "");
    ref.onsubmit = async (e) => {
      e.preventDefault();
      let errored = false;

      for (const k in fields) {
        const field = fields[k];
        await checkValid(field, setErrors, errorClass)();
        if (!errored && field.element.validationMessage) {
          field.element.focus();
          errored = true;
        }
      }
      !errored && callback(ref);
    };
  };

  return { validate, formSubmit, errors };
}

input {
  display: inline-block;
  padding: 4px;
  margin-top: 10px;
  margin-bottom: 10px;
}
.error-message {
  color: red;
  padding: 8px;
}
.error-input {
  box-shadow: 0px 0px 2px 1px red;
}
